class Solution {
    public int maxOperations(String s) {
        int operation = 0;

        int zero = 0, one = 0;
        for(int i=0 ;i<s.length(); i++) {
            if(s.charAt(i) == '0') {
                zero += 1;
            } else {                
                if(zero > 0) {
                    operation += one;
                }
                one += 1;
                zero = 0;
            }
        }
        if(zero > 0) {
            operation += one;
        }
        return operation;
    }
}
