class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int[] arr = new int[n+1];
        int x = 0;
        while(x < n && s.charAt(x) == '0'){
            x++;
        }

        int y = n-1;
        while(y >= 0 && s.charAt(y) == '1'){
            y--;
        }
        y++;
        // System.out.println(x + " " + y);
        if(y-x < 1){
            return 0;
        }
        int prev = x;
        for(int i = x+1; i < y; i++){
            if(s.charAt(i) == '1'){
                arr[prev] = i;
                prev = i;
            }
        }

        if(prev != n){
            arr[prev] = y;
        }

        int[] dp = new int[n+1];
        // dp[y] = -1;
        for(int i = y-1; i >= 0; i--){
            if(s.charAt(i) == '1'){
                int diff = arr[i] - i;
                if(diff > 1){
                    dp[i] = dp[arr[i]] + 1;
                } else {
                    dp[i] = dp[arr[i]];
                }
            }
        }
        // System.out.println(Arrays.toString(arr));
        // System.out.println(Arrays.toString(dp));
        int sum = 0;
        for(int i : dp){
            sum += i;
        }
        return sum;
    }
}
