class Solution {
public:
    int maxOperations(string s) {
        s += '1' ;
        int n = s.length() , ans = 0 ;
        int count = (s[0] == '1') ? 1 : 0 ;
        for(int i=1 ; i<n ; i++){
            if(s[i] == '1' && s[i-1] == '0') ans += count ;
            if(s[i] == '1') count++ ;
        }
        return ans ;
    }
};
