class Solution {
public:
    int maxOperations(string s) { 

        int n = s.length();

        vector<int> vv(n); 

        int count = 0; 


        for(int i = n-2; i>=0; i-- ){

            if(s[i] != s[i+1]){ 

                if(s[i] == '1'){
                    vv[i] = count+1;
                    count++;
                } 

            } 

            else{ 

                if(s[i] == '1'){
                    vv[i] = count;
                }

            }
        }  



        int ans = 0; 


        for(int i = 0; i<n; i++){
            // cout<<vv[i]<<" ";
            ans += vv[i];
        } 
//         cout<<endl;


        return ans ;

    }
};
