    public int maxOperations(String s) {
        int ans=0,n=s.length();
        int prev=0;
        for(int i=0;i<n;i++)
        {
            char c = s.charAt(i);
            if(c=='0')
                continue;
            int index=i,len=0;
            while(index<n && s.charAt(index)=='1')
            {
                index++;len++;
            }
            if(index==n)
                return ans;
            ans=ans+prev+len;
            prev=prev+len;
            i=index-1;
        }
        return ans;
    }
