class Solution {
public:
    int maxOperations(string s) {

        int o = 0, n = s.size();

        for(int i=0; i<n; ++i) if(s[i]=='1') o++;

        string str(n, '0');
        int ind = n - o;
        for(int i=ind; i<n; ++i) str[i] == '1';

        if(str == s) return 0;

        int ones = 0;
        int ans = 0;
        for(int i=0; i<n; ){
            if(s[i++]=='1') ++ones;
            else{
                while(s[i]=='0'){
                    i++;
                }
                // cout<<ones<<" ";
                ans += ones;
            }
        }
        return ans;
    }
};
