class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0,cc=0,f=0,n=s.size();
        for(int i = 0;i<n;i++){
            if(s[i]=='1'){
                if(!f){f=1;}
                else if(f==2){
                    cnt+=cc;
                    f=1;
                }
                cc++;
            }
            else{
                f=2;
                if(i==n-1)return cnt+=cc;
            }
        }
        return cnt;
    }
};
