class Solution {
    public int maxOperations(String s) {
        char[] sh = s.toCharArray();
        List<Integer> nums = new ArrayList<>();
        int n = sh.length;
        for(int i = 0; i < n; ++i){
            if(sh[i] == '1'){
                int count = 0, j = i;
                for(; j < n; ++j){
                    if(sh[j] == '1') count ++;
                    else break;
                }
                nums.add(count);
                i = j - 1;
            }
        }
        int sum = 0, res = 0;
        int maxx = sh[n - 1] == '0'? nums.size(): nums.size() - 1;
        for(int i = 0; i < maxx; ++i){
            sum += nums.get(i);
            res += sum;
        }
        return res;
    }
}
