class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        int ans=0;
        bool counted=false;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                cnt++;
                counted=false;
            }
            else {
                if(!counted){
                    ans+=cnt;
                    counted=true;
                }
            }
        }
        return ans;
    }
};
