class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int t = n;
        int ans = 0;
        int y = 0;
        int r = 0;
        for(int i =0;i<n;i++)
        {
            if(s[i]=='1')
            {
                y++;
            }
        }

        for(int i = s.size()-1 ; i>=0; i--)
        {
            if(s[i]=='1')
            {

                if(t - i == 1)
                {
                    t = i;

                }
                else
                {
                    //cout<<i<<" "<<y<<" "<<r<<endl;
                    ans = ans + (y - r);
                    t = i;
                }
                r ++;
            }
        }
        return ans;
    }
};
