class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        int ans=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                cnt+=1;
            }else{
                ans+=cnt;
                while(i<s.size()-1 and s[i+1]!='1'){
                    i++;
                }
            }
        }
        return ans;
    }
};
