class Solution {
    public int maxOperations(String s) {
        int res = 0, on = 0;
        boolean chk = true;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                on++;
                chk = true;
            } else {
                if (chk) res += on;
                chk = false;
            }
        }
        return res;
    }
}
