class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ans = 0;
        int start = 0;
        bool flag = true;
        for(int i=0; i<n; i++){
            if(s[i]=='1'){start++; flag=true;}
            else{if(flag)ans+=start; flag=false;}
        }
        return ans;
    }
};
