class Solution {
public:
    int maxOperations(string s) {
        int cnt=0,ans=0,prev=0;
        for(int i=0;i<s.size();++i){
            if(s[i]=='1'||i+1==s.size()){
                ans+=prev;
                cnt++;
                prev=0;
            }
            if(i+1<s.size()&&s[i]=='1'&&s[i+1]=='0'){
                prev=cnt;
            }
        }
        return ans;
    }
};
