class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), one = 0,  z = 0;
        int res = 0;

        for (auto& c : s) {
            if (c == '0') {
                z++;
            } else {
                if (one > 0 && z > 0) {
                    res += one;
                }

                one++;
                z = 0;
            }
        }

        if (one > 0 && z > 0) {
            res += one;
        }

        return res;
    }
};
