class Solution {
    public int maxOperations(String s) {
        s += "1";

        int res = 0;
        int pre = 0;

        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                if (i > 0 && s.charAt(i - 1) == '0') {
                    res += pre;
                }
                pre++;
            }
        }
        return res;
    }
}
