class Solution {
public:
    int maxOperations(string s) {
        int a = 0,b = 0;
        int ans = 0;
        int k = s.length() - 1;
        while(s[k] == '1' && k >0) k--;
        if(k == 0) return 0;
        for(int i=k;i>=0;i--){
            if(s[i] == '1'){
                a++;
            }
            else if(s[i] == '0' && a){
                b++;
                ans += a * b;
                a = 0;
            }

        }
        b++;
        ans += a * b;
        return ans;
    }
};
