class Solution {
public:
    int maxOperations(string s) {
        int count=0;
        int ans=0;
        int ones=0;
        bool flag=false;
        for(int i=0;i<s.length();i++)
        {
            if(s[i]=='1') 
            {
                ones++;
                flag=true;
            }
            else if(s[i]=='0' && flag==true)
            {
                ans+=ones;
                flag=false;
            }
        }
        return ans;
    }
};
