class Solution {
    public int maxOperations(String s) {
        int n =s.length();
        int ans=0;
        int d=0;
        int []arr =new int[n];
        for(int i=0;i<n;i++){
            arr[i] = s.charAt(i)-'0';
        }
        for(int i=n-1;i>=0;i--){
            if(arr[i]==1){
                int f =-1;
                int j=i+1;
                while(j<n && arr[j]==0){
                    f++;
                    j++;
                }
                if(f!=-1){
                    d++;
                    ans+=d;
                }else{
                    ans+=d;
                }

            }
        }

        return ans;
    }
}
