class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int pt=n-1;
        int ans=0;int zero=0,ones=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='0')
            {
                while(i<n-1 and s[i+1]!='1')
                i++;
                ans+=ones;
            }
            else ones++;
        }
        return ans;
    }
};
