class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        bool k=0;
        int p=0;
        int r=0;
        int book=0;
        vector<int> h;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                h.push_back(1);
                k=1;
            }
            if(s[i]=='0' && k==1){
                h.push_back(0);
                k=0;
            }
        }
        for(auto it:h){
            if(it==1) p++;
            else ans+=p;
        }
        return ans;
    }
};
