typedef long long ll;
class Solution {
public:
    int maxOperations(string s) {
        ll ans = 0,pf = 0,n = s.size();

        for(int i = 0;i<n;i++){
            if(s[i] == '1'){
                ll j = i;
                while(j < n && s[j] == s[i])j++;

                if(j < n && s[j] == '0'){
                    ll ct = j - i;
                    pf += ct;
                    ans += pf;
                }
                i = j;
            }
        }

        return ans;
    }
};
