class Solution {
public:
    int maxOperations(string s) {
        int ct=0,ans=0;
        char last='$';
        for(auto i:s){
            if(i=='1') ct++;
            if(last=='1' and i=='0'){
                ans+=ct;
            }
            last=i;
        }
        return ans;
    }
};
