class Solution {
    public int maxOperations(String s) {
        int ans=0;
        int cnt=0;
        int[] arr=new int[s.length()];
        if(s.charAt(s.length()-1)=='0'){
            arr[s.length()-1]=1;
            cnt++;
        }
        for(int i=s.length()-2;i>=0;i--){
            if(s.charAt(i)=='0' && s.charAt(i+1)!='0'){
                cnt++;
            }
            arr[i]=cnt;
        }
        for(int i=0;i<arr.length;i++){
            if(s.charAt(i)=='1'){
                ans+=arr[i];
            }
        }
        return ans;
    }
}
