class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int c=0;
        int fl=0;
        int ans=0;
        for(int i=n-1;i>=0;i--)
        {
            if(s[i]=='0')
            {
                fl=1;
            }
            else
            {

                if(fl==1)
                {
                    c++;
                }
                fl=0;
                 ans+=c;
            }
            // cout<<i<<" "<<c<<"\n";
        }
        return ans;
    }
};
