class Solution {
    public int maxOperations(String s) {
        List<Integer> ids = new ArrayList<>();
        int i =0 ;
        for(char c: s.toCharArray()) {
            if(c == '1') ids.add(i);
            i++;
        }
        int n = s.length();
        int ans = 0;

        for(int j=0;j<ids.size();j++) {
            if(j!=ids.size()-1 && ids.get(j)+1<ids.get(j+1)) {
                ans += j+1;
            } else if(j==ids.size()-1 && ids.get(j) < n-1) {
                ans += j+1;
            }
        }
        return ans;
    }
}
