class Solution {
    public:
    int maxOperations(string s) {
        int cnt = 0, ones = 0, flag = 0;

        for(int i=0;i<s.length();i++){
            if (s[i] == '1') {
                ones++;
                flag = 1;
            } else if (s[i] == '0' && flag) {
                cnt += ones;
                flag = 0;
            }
        }

        return cnt;
    }
};
