class Solution:
    def maxOperations(self, s: str) -> int:


        stack = []
        push = False
        res = 0

        for i in range(len(s)):
            if s[i] == '1':
                if push == True:
                    res += len(stack)
                    push = False
                stack.append(1)
            else:
                push = True
                if i == len(s)-1 and s[i] == '0':
                    res += len(stack)


        return res
