class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int[] one = new int[n + 1];
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == '1') {
                one[i + 1] = one[i] + 1;
            } else {
                one[i + 1] = one[i];
            }
        }
        int res = 0;
        int index = n - 1;
        while (index >= 1) {
            if (s.charAt(index) == '0' && s.charAt(index - 1) == '1') {
                res += one[index];
            }
            index--;
        }
        return res;
    }
}
