class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        vector<int>store1;
        int j=0;
        while(j<n && s[j]!='1'){
            j++;
        }
        if(j==n)
             return 0;
        int f=0;
        while(j<n){
            if(s[j]=='0'){
                f=1;
               break;
            }
            j++;
        }
        if(f==0)
             return 0;
        for(int i=0;i<n;i++){
            if(s[i]=='1')
                 store1.push_back(i);
        }
        int cnt=0,ans=0;
        for(int i=0;i<store1.size()-1;i++)
        {
            int pos=store1[i];
            int jana=store1[i+1]-1;
            cnt++;
            if(jana!=pos)
                 ans+=cnt;
        }
        if(store1[store1.size()-1]!=n-1){
           //if(store[i]){
            cnt++;
               ans+=cnt;
           //}
        }
        return ans;
    }
};
