class Solution {
public:
    int maxOperations(string s) {
        if(s[s.size() -1] == '0')s+='1';
        int cnt=0;
        for(int i=1;i<s.length(); i++){
            if(s[i] == '1' && s[i-1] =='0')cnt++;
        }
        cout<<cnt;
        int ans=0;
        for(int i=0;i<s.length(); i++){
            if(i-1 >=0 && s[i] == '1' && s[i-1] =='0')cnt--;
            if(s[i] == '1')ans+=cnt;
            if(cnt==0)break;
        }
        return ans;
    }
};
