class Solution {
public:
    int maxOperations(string s) 
    {
        int ans=0,p=0;
        for(int i=s.size()-2;i>=0;i--)
        {
            if(s[i]=='1')
            {
                if(s[i+1]=='0')ans+=p+1,p++;
                else ans+=p;
            }
        }
        return ans;

    }
};
