class Solution {
public:
    int maxOperations(string s) {
        int shiftCount=0;
        int operation=0;
        if(s[0]=='1')
            shiftCount++;

        for(int i=1;i<s.length();i++)
        {
            if(s[i]=='1')
                shiftCount++;
            else
            {
                while(i<s.length()&&s[i]=='0')
                {
                    i++;
                }
                i--;
                operation+=shiftCount;
            }
        }
        return operation;
    }
};
