class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int j=0;
        int res=0;
        for(int i=n-2;i>=0;i--){
            if(s[i]=='1' && s[i+1]=='1'){
                res+=j;
            }
            else if(s[i]=='1' && s[i+1]=='0'){
                j++;
                res+=j;
            }
        }
        return res;
    }
};
