class Solution{
public int maxOperations(String s) {
    int res = 0;
    int onesCount = 0;
    boolean isOnesGroup = true;

    for (int i = 0; i < s.length(); i++) {
        if (s.charAt(i) == '1') {
            onesCount++;
            isOnesGroup = true;
        } else {
            if (isOnesGroup)
                res += onesCount;
            isOnesGroup = false;
        }
    }

    return res;
}
}
