class Solution {
public:
    int maxOperations(string s) {
        int operations = 0, temp = 0;
        bool hasOne = false;
        vector<int> vec;

        for (char ch : s){
            if( ch == '1'){
                vec.push_back(1);
                hasOne = true;
            }
            else if(ch == '0' && hasOne){
                vec.push_back(0);
                hasOne = false;
            }
        }

        for(auto bit : vec){
            if(bit == 1) temp += 1;
            else operations += temp;
        }

        return operations;
    }
};
