class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        long long sum=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                cnt++;
                if(i+1<s.size()&&s[i+1]=='0')
                sum+=cnt;
            }
        }
        return sum;
    }
};
