class Solution {
    public int maxOperations(String s) {
        int operations = 0;
        int onesCount = 0;
        boolean changed = false;
        for (char c : s.toCharArray()) {
            if (c == '1') {
                onesCount++;
                changed = true;
            } else if (c == '0') {
                if (onesCount > 0 && changed) {
                    operations += onesCount;
                    changed = !changed;
                }
            }
        }
        return operations;
    }
}
