class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        vector<int> cnts;
       for(int i = 0; i < s.size();i++){
        if(s[i] == '0'){
            if(cnt > 0){
                cnts.push_back(cnt);
            }
            cnt = 0;
        }else{
            cnt++;
        }
       } 

        int sum = 0;
        int ans = 0;
        for(int i = 0; i < cnts.size(); i++){
            sum += cnts[i];
            ans += sum;
        }
        return ans;
    }
};
