class Solution {
public:
    int maxOperations(string s) {
        int c=0;
        int ans=0;
        for(int i=0;i<s.length();){
              if(s[i]=='0'){
                while(s[i]=='0'){
                    i++;
                }
                ans=ans+c;     
              }else{
                c++;
                i++;
              }
        }
        return ans;
    }
};
