class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int total = 0;
        int i = 0;
        int res = 0;

        while (i < n) {
            if (s[i] == '1') {
                total++;
                i++;
            } else {
                while (i < n && s[i] == '0') {
                    i++;
                }
                res += total;
            }
        }

        return res;
    }
};
