class Solution {
public:
    int maxOperations(string s) {
        int l = s.length();
        int count = 0;
        int i = 0;

        int score = 0;
        bool scoreInc = false;
        for(int i=l-1; i>=0; i--) {
            if(s[i] == '0' && scoreInc == false) {
                scoreInc = true;
                score++;
            }

            if(s[i] == '1') {
                scoreInc = false;
                count += score;
            }
        }


        cout << s;
        return count;;
    }
};
