class Solution {
public:
    int maxOperations(string s) {
        vector< pair<char,int> > cache;
        for(auto ele : s)
        {
            if(cache.empty())
                cache.push_back({ele,1});
            else if(cache.back().first==ele)
                cache.back().second++;
            else
                cache.push_back({ele,1});
        }

        long long ones=0ll;
        long long ans=0ll;
        for(auto &[type,freq] : cache)
        {
            if(type=='0')
                ans=ans+ones;
            else
                ones=ones+freq;
        }
        return ans;
    }
};
