class Solution {
public:
    int maxOperations(string s) {
        // 0011101
        // 0011011
        // 0010111
        // 0001111
         int count=0;
         int ans=0;
         bool check=false;
        for(int i=0;i<s.length();i++){
            if(s[i]=='1'){count++;check=true;}
            else {if(check){ans+=count;}check=false;}
        }
        return ans;
    }
};
