class Solution {
    public int maxOperations(String s) {
        int result = 0;
        int i = s.length() - 2;
        int prevCount = 0;
        while(i>=0) {
            if(s.charAt(i) == '1') {
                if (s.charAt(i + 1) == '0') {
                    prevCount++;
                }
                result = result + prevCount;    
            }
            i--;
        }
        return result;
    }
}
