class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        s += "1";
        int n = s.length(), cnt = 0;
        for (int i = 0; i < n; ) {
            if (s[i] == '1') {
                ans += cnt;
                while (i < n && s[i] == '1') {
                    ++cnt;
                    ++i;
                }
            } else {
                ++i;
            }
        }
        return ans;
    }
};
