class Solution {
public:
    int maxOperations(string s) {
        int n = s.size ();
        int ans = 0, cnt = 0;
        auto isAdd = [&] (int i)->bool
        {
            if (i == n-1)
                return true;
            if (i && s[i+1] == '1')
                return true;
            return false;
        };
        for (int i = 0; i < n; ++i)
        {
            if (s[i] == '1')
                cnt++;
            else if (s[i] == '0' && isAdd (i))
                ans += cnt;
        }
        return ans;
    }
};
