class Solution:
    def maxOperations(self, s: str) -> int:

        moves = 0
        snew = s[::-1]

        i = 0
        ans = 0
        while(i < len(s)):
            if(snew[i] == '1'):
                ans += moves
                i += 1
            else:
                while(i < len(snew) and snew[i] == '0'):
                    i += 1
                moves += 1
        return ans
