class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt_one = 0, res = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') continue;
            cnt_one++;
            if (s[i + 1] == '0') {
                res += cnt_one;
            }
        }
        return res;
    }
};
