class Solution {
public:
    vector<bool> v;
    int maxOperations(string s) {
        int x = 0;
        vector<int> ans(1,0);
        int n = s.size();
        signed int flag = 0;

        for (size_t i = 0; i < n; i++) {
            if (s[i] == '1') {
                v.push_back(1);
                flag = 1;
            } else if (s[i] == '0' and flag == 1) {
                v.push_back(0);
                flag = 0;
            }
        }

        for (auto i : v) {
            if (i == 1) x++;
            else ans[0] += x;
        }

        return ans[0];
    }
};
